     })
}  }

 render(){
let {isShowForm} = this.props;
if(isShowForm===false) 
      return null;
else 
  {
    return (
<div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
 <div className="panel panel-warning">
                    <div className="panel-heading">
                        <h3 className="panel-title">Thêm Công Việc</h3>
                    </div>
                    <div className="panel-body">
                        <form onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <label>Tên :</label>
                                <input name="task_name" value={this.state.task_name} onChange={this.handleChange} type="text" className="form-control" />
                            </div>
                            <label>Trạng Thái :</label>
                            <select name="task_status" value={this.state.task_status} onChange={this.handleChange} className="form-control">
                                <option value={1}>Kích Hoạt</option>
                                <option value={0}>Ẩn</option>
                            </select>
                            <br/>
                            <div className="text-center">
                                <button type="submit" className="btn btn-warning">Thêm</button>&nbsp;
                                <button onClick={this.onCloseForm} type="button"className="btn btn-danger">Hủy Bỏ</button>
                            </div>
                        </form>
                    </div>
                </div>
</div>
);
}
}
}
const mapStatetoProps = state => {
return { 
    isShowForm:state.isShowForm,

}
}
const mapDispatchtoProps = (dispatch,ownProps) =>{
  return {
    onCloseForm: () =>{
      dispatch(actCloseForm());

    }, 
    handleSubmit:(item)=>{
      dispatch(actSubmitForm(item),
       dispatch(actCloseForm())
      )
    }
  }
}



export default connect(mapStatetoProps,mapDispatchtoProps)(Form);
