import React, { Component } from 'react';
import {connect} from "react-redux"; 
import {actFilterStatus} from './../actions/index';
class StatusFilter extends Component {
    constructor(props) {
        super(props);
        this.state = {status: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleFilter = this.handleFilter.bind(this);
      }
    handleChange(event) {
        this.setState({status: event.target.value});
      }
    handleFilter(status) {
        // this.props.handleFilter(this.state.status)
        alert('Your favorite flavor is: ' + this.state.status);
        console.log(this.state.status)
        event.preventDefault();
      }
    render() {
        console.log(this.state.status)
        return (
            <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
            <form>
             <select name="status" value={this.state.status} onChange={this.handleChange} className="custom-select">
				<option onClick={()=>this.handleFilter('')} value={''}>All</option>
				<option onClick={()=>this.handleFilter(1)} value={1}>Active</option>
				<option onClick={()=>this.handleFilter(0)} value={0}>InActive</option>
             </select>  
         </form> 
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
     status: state.statusFilter
    }
  }
  const mapDispatchToProps = (dispatch,ownProps) => {
      return {
        handleFilter: (status) =>{
              dispatch(actFilterStatus(status))
          }
      }
  }

export default connect(mapStateToProps,mapDispatchToProps)(StatusFilter);
