class Search extends React.Component {
constructor(props){
	super(props);
	this.state={
		strSearch: ''
	}
	this.handleSearch = this.handleSearch.bind(this);
	this.handleChange = this.handleChange.bind(this)
}

handleSearch(){
	console.log(this.state.strSearch)
}
handleClear(){
	console.log('dfa')
}
handleChange(event){
	this.setState({
		strSearch:event.target.value})
	}

render(){
return (

<div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
	<div className="input-group">
		<input value={this.state.strSearch} onChange={this.handleChange}type="text" className="form-control" placeholder="Nhập từ khóa..." 
		ref="search"/>
		<span className="input-group-btn">
			<button onClick={this.handleSearch}className="btn btn-primary" type="button">
			<span className="fa fa-search mr-5" />Tìm
				</button>
				<button onClick={this.handleClear}className="btn btn-warning ml-2" type="button">
			<span className="fa fa-search mr-5" />Clear
				</button>
			</span>


	</div>
</div>

	)
	}
}
export default Search;
