import * as types from './../constants/ActionTypes.js';

let inititalDefault =3; 

const statusFilter = (state =inititalDefault ,action) => {
		switch(action.type) {
			case types.FILTER_STATUS:
				return action.status
			default:
				return state
		}
}
export default statusFilter;
