import React from 'react';
import Search from './Search';
import Sort from './Sort';

class  Control extends React.Component {
constructor(props){
		super(props);
		this.onToggleForm = this.onToggleForm.bind(this);
		this.onCloseForm = this.onCloseForm.bind(this)
		this.onHandleSearch = this.onHandleSearch.bind(this)
}
onToggleForm(){
this.props.onToggleForm();

}
onCloseForm(){
	this.props.onCloseForm();
}


render(){

let elmButton = <button onClick = {this.onToggleForm} type="button" className="btn btn-primary mb-3">
              <span className="fa fa-plus" />Thêm Công Việc
    </button>; 
if(this.props.isShowForm==true){
	elmButton =  <button onClick = {this.onCloseForm}type="button" className="btn btn-success mb-3">
              <span className="fa fa-plus" />Close Form
    </button>
}
return (
<div>
	<div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
	{elmButton}
    </div>
<div className="row mt-15">
	{/*FIND: START*/}
	<Search onHandleSearch = {this.props.onHandleSearch}/>
		{/*FIND: END*/}
		{/*SORT: START*/}
	<Sort />	
</div>
	</div>
			);
			}}
			export default Control;
