import React, { Component } from 'react';
import {connect} from "react-redux"; 
import {actFilterStatus} from './../actions/index';
class StatusFilter extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleFilter = this.handleFilter.bind(this);
      }
    handleChange(event) {
        this.setState({value: event.target.value});
      }
    handleFilter(event) {
        console.log(this.state.value)
        event.preventDefault();
      }
    render() {
     let {status} = this.props;
     console.log(status)
        return (
            <div className="col-xs-8 col-sm-8 col-md-8 col-lg-8">
            <form onSubmit={this.handleFilter}>
             <select name="value" value={this.state.value} onChange={this.handleChange} className="custom-select">
				<option value={''}>All</option>
				<option value={1}>Active</option>
				<option value={2}>InActive</option>
             </select>  
         </form> 
            </div>
        );
    }
}
const mapStateToProps = state => {
    return {
     status: state.statusFilter
    }
  }
  const mapDispatchToProps = (dispatch,ownProps) => {
      return {
        handleFilter: (status) =>{
              dispatch(actFilterStatus(status))
          }
      }
  }

export default connect(mapStateToProps,mapDispatchToProps)(StatusFilter);
