import * as types from './../constants/ActionTypes.js';
import * as config from './../constants/Config';
import {remove,reject} from 'lodash';
let inititalDefault =[
{
	id:1,
	name:'reactjs',
	status:0
},
	{
	id:2,
	name:'html&css',
	status:1
}
]; 
let tasks = JSON.parse(localStorage.getItem(config.ITEMS_FROM_LOCAL_STOGARE));
inititalDefault = (tasks!==null&&tasks.length>0)? tasks:inititalDefault;

const items = (state =inititalDefault ,action) => {
	let id = null; 
	switch(action.type){
		case types.DELETE_ITEM:
		id = action.id;
		remove(state,(item)=>{
    	return item.id === id
  });
		localStorage.setItem(config.ITEMS_FROM_LOCAL_STOGARE,JSON.stringify(state))
		return [...state];
		default:
			return state
	}
}
export default items;
