import React from 'react';
import Search from './Search';
import Sort from './Sort';
import { connect } from 'react-redux'; 
import isShowForm from './../reducers/isShowForm';
import {actToggleForm} from './../actions/index';
class  Control extends React.Component {
constructor(props){
	super(props);

		this.onToggleForm = this.onToggleForm.bind(this)	
}
onToggleForm(){
	this.props.onToggleForm();
}
render(){
let {isShowForm} = this.props
let elmButton = null;
if(isShowForm) {
elmButton = <button onClick = {this.onToggleForm} type="button" className="btn btn-success mb-3">
              <span className="fa fa-plus" />Close Form</button>
} else {
elmButton = <button onClick = {this.onToggleForm} type="button" className="btn btn-primary mb-3">
              <span className="fa fa-plus" />Add Form</button>
}
return (
<div>
	<div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
		{elmButton}
    </div>
<div className="row mt-15">
	{/*FIND: START*/}
	<Search/>

	<Sort />	
</div>
	</div>
			);
			}}
const mapStateToProps = state => {
  return {
    isShowForm: state.isShowForm,
  }
}
const mapDispatchToProps = (dispatch,ownProps) => {
	return {
		onToggleForm: () =>{
			dispatch(actToggleForm())
		}
	}
}
export default connect(mapStateToProps,mapDispatchToProps)(Control);
